use leptos::{IntoView, component, view};

use crate::{BODY_TEXT, HEADING_TEXT, TITLE_TEXT};

#[component]
pub fn Hero() -> impl IntoView {
    view! {
        <div class="flex flex-row gap-8 justify-center w-full portrait:flex-col-reverse portrait:md:flex-row h-fit">
            <div class="basis-1/2 portrait:basis-full portrait:md:basis-1/2 xl:basis-5/12">
                <HeroText/>
            </div>
            <div class="basis-1/2 portrait:basis-full portrait:md:basis-1/2 xl:basis-5/12">
                <HeroSvg/>
            </div>
        </div>
    }
}

#[component]
fn HeroText() -> impl IntoView {
    view! {
        <div class="flex flex-col gap-6 lg:gap-8 text-balance">
            <h1 class=TITLE_TEXT>{"Stephen Power"}</h1>
            <h2 class=HEADING_TEXT>{"Frontend Developer"}</h2>
            <p class=BODY_TEXT>
                {"Hi, I'm Stephen. I make open-source applications for the web, mobile and desktop, usually with Rust."}
            </p>
        </div>
    }
}

